import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const RecipientPage = () => {
  const [cart, setCart] = useState({});
  const navigate = useNavigate();

  const foodList = [
    { id: 1, name: "Dosa", price: 10, image: "data:image/jpeg;base64,"},
    { id: 2, name: "Chicken PepperFry", price: 5, image:"data:image/jpeg;base64,"},
    { id: 3, name: "Biryani", price: 150, image: "data:image/jpeg;base64," },
    { id: 4, name: "Chicken Curry", price: 180, image: "data:image/jpeg;base64," },
    { id: 5, name: "Dabul Ka Meeta", price: 6, image: "data:image/jpeg;base64," },
    { id: 6, name: "Ice Cream", price: 4, image: "data:image/jpeg;base64," },
  ];

  const handleAddToCart = (foodId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [foodId]: (prevCart[foodId] || 0) + 1,
    }));
  };

  const handleRemoveFromCart = (foodId) => {
    setCart((prevCart) => {
      const updatedCart = { ...prevCart };
      if (updatedCart[foodId] > 1) {
        updatedCart[foodId]--;
      } else {
        delete updatedCart[foodId];
      }
      return updatedCart;
    });
  };

  const totalItems = Object.values(cart).reduce((sum, count) => sum + count, 0);

  return (
    <div className="container py-5">
      <h1 className="display-4 text-center mb-4">Available Food for Recipients</h1>

      <div className="row">
        {foodList.map((food) => (
          <div key={food.id} className="col-md-4 mb-4">
            <div className="card shadow-sm">
              <img
                src={food.image}
                alt={food.name}
                className="card-img-top"
                style={{ height: "200px", objectFit: "cover" }}
              />
              <div className="card-body text-center">
                <h5 className="card-title">{food.name}</h5>
                <p className="card-text text-muted fw-bold">${food.price}</p>
                <div className="d-flex justify-content-center">
                  <button
                    className="btn btn-success btn-sm mx-1"
                    onClick={() => handleAddToCart(food.id)}
                  >
                    +
                  </button>
                  <span className="mx-2">{cart[food.id] || 0}</span>
                  <button
                    className="btn btn-danger btn-sm mx-1"
                    onClick={() => handleRemoveFromCart(food.id)}
                    disabled={!cart[food.id]}
                  >
                    -
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <h2 className="mt-5 text-center">
        Cart: <span className="text-success">{totalItems}</span> item(s)
      </h2>

      {totalItems > 0 && (
        <div className="text-center mt-4">
          <button
            className="btn btn-primary btn-lg"
            onClick={() => navigate("/payment", { state: { cart } })}
          >
            Proceed to Payment
          </button>
        </div>
      )}
    </div>
  );
};

export default RecipientPage;
